var haloDebounce = (function () {
  'use strict';

  /**
   * @description: 防抖函数
   * @param {Function} fn
   * @param {Number} time
   */
  var timer;
  function debounce(fn, time) {
      if (time === void 0) { time = 300; }
      if (typeof fn !== "function") {
          throw new Error("必须传入一个函数作为参数");
      }
      timer && clearTimeout(timer);
      timer = setTimeout(function () {
          fn();
      }, time);
  }

  return debounce;

}());
