var haloFullScreen = (function () {
  'use strict';

  /**
   * @description: 是否全屏
   * @return: Boolean
   */
  function isFullScreen() {
      var fsDoc = document;
      return !!(fsDoc.fullscreen ||
          fsDoc.webkitIsFullScreen ||
          fsDoc.mozIsFullScreen ||
          fsDoc.msIsFullScreen);
  }
  /**
   * @description: 设置全屏
   */
  function setFullScreen() {
      if (isFullScreen())
          return;
      var ele = document.documentElement;
      var requestFullscreen = ele.requestFullscreen ||
          ele.webkitRequestFullscreen ||
          ele.msRequestFullscreen ||
          ele.mozRequestFullScreen ||
          null;
      requestFullscreen && requestFullscreen.call(ele);
  }
  /**
   * @description: 关闭全屏
   */
  function closeFullScreen() {
      if (!isFullScreen())
          return;
      var doc = document;
      var exitFullscreen = doc.exitFullscreen ||
          doc.msExitFullscreen ||
          doc.mozCancelFullScreen ||
          doc.webkitExitFullscreen ||
          null;
      exitFullscreen && exitFullscreen.call(doc);
  }
  /**
   * @description: 切换全屏
   */
  function toggleFullScreen() {
      if (isFullScreen()) {
          closeFullScreen();
      }
      else {
          setFullScreen();
      }
  }
  var fullScreen = {
      is: isFullScreen,
      set: setFullScreen,
      close: closeFullScreen,
      toggle: toggleFullScreen
  };

  return fullScreen;

}());
