var haloThrottle = (function () {
  'use strict';

  var timer;
  /**
   * @description: 函数节流
   * @param {fn} Function
   * @param {time} Number 截流时间
   */
  function throttle(fn, time) {
      if (time === void 0) { time = 300; }
      if (typeof fn !== "function") {
          throw new Error("必须传入一个函数作为参数");
      }
      if (!timer) {
          timer = window.setTimeout(function () {
              fn();
              timer = 0;
          }, time);
      }
  }

  return throttle;

}());
